"use strict";function main(){buildGalleries();}
function buildGalleries(){var galleries=document.querySelectorAll(".gallery");for(var gallery of galleries){buildGallery(gallery);}}
function buildGallery(gallery){var images=gallery.getAttribute("gallery-images").trim().split("  ");if(!images||images.length===0)
return;loadImage(gallery,images,0);setInterval(function(){var index=Number(gallery.getAttribute("current-image"));index++;if(index>=images.length)index=0;loadImage(gallery,images,index);},5000);}
function loadImage(gallery,images,index){var src=images[index];gallery.setAttribute("current-image",String(index));var elem=gallery.querySelector(".gallery__image");if(!elem){elem=document.createElement("img");elem.className="gallery__image";gallery.appendChild(elem);}
elem.setAttribute("src",src);}
function clickLogo(){var menu=document.querySelector(".menu");var backdrop=document.querySelector(".menu__backdrop");if(menu.classList.contains("menu_active")){menu.classList.remove("menu_active");backdrop.classList.remove("menu__backdrop_active");}else{menu.classList.add("menu_active");backdrop.classList.add("menu__backdrop_active");}}